@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@400;500;600;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Light Mode Colors */
    --primary: #6366F1;
    --primary-hover: #4F46E5;
    --primary-light: #818CF8;
    --primary-bg: #EEF2FF;
    --secondary: #8B5CF6;
    --accent: #EC4899;
    --success: #10B981;
    --warning: #F59E0B;
    --error: #EF4444;
    --info: #3B82F6;
    --background: #F9FAFB;
    --surface: #FFFFFF;
    --surface-elevated: #FFFFFF;
    --text-primary: #111827;
    --text-secondary: #6B7280;
    --text-tertiary: #9CA3AF;
    --border: #E5E7EB;
    --border-hover: #D1D5DB;

    /* Shadows */
    --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
    --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1);

    /* Typography */
    --font-primary: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
    --font-mono: 'Fira Code', 'Courier New', monospace;

    /* Transitions */
    --transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
    --transition-base: 250ms cubic-bezier(0.4, 0, 0.2, 1);
    --transition-slow: 350ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .dark {
    /* Dark Mode Colors - Enhanced for Chat UI */
    --primary: #818CF8;
    --primary-hover: #6366F1;
    --primary-light: #4F46E5;
    --primary-bg: #1E1B4B;
    --secondary: #A78BFA;
    --accent: #F472B6;
    --success: #34D399;
    --warning: #FBBF24;
    --error: #F87171;
    --info: #60A5FA;
    --background: #0F172A; /* Current dark background */
    --surface: #1E293B;   /* Slightly lighter for UI elements */
    --surface-elevated: #334155; /* For elevated elements like cards */
    --text-primary: #F1F5F9; /* Very light gray for main text */
    --text-secondary: #CBD5E1; /* Light gray for secondary text */
    --text-tertiary: #94A3B8; /* Medium gray for tertiary text */
    --border: #334155; /* Border color matching surface-elevated */
    --border-hover: #475569;

    /* Dark Mode Shadows */
    --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.5);
    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.5);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.5);
    --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.5);
  }

  * {
    @apply border-neutral-200 dark:border-neutral-700;
  }

  body {
    background-color: var(--background);
    color: var(--text-primary);
    font-family: var(--font-primary);
    @apply antialiased;
    transition: background-color var(--transition-base), color var(--transition-base);
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  .glass {
    @apply bg-white/95 backdrop-blur-lg border border-neutral-200/50 shadow-sm;
  }

  .glass-dark {
    @apply bg-neutral-900/95 backdrop-blur-lg border border-neutral-700/50 shadow-lg;
  }

  .glass-frosted {
    @apply bg-white/90 backdrop-blur-md border border-neutral-200/60;
  }

  .gradient-primary {
    @apply bg-gradient-to-r from-primary-500 to-primary-600;
  }

  .gradient-accent {
    @apply bg-gradient-to-r from-accent-500 to-accent-600;
  }

  .gradient-mesh {
    background: 
      radial-gradient(at 0% 0%, rgba(59, 130, 246, 0.08) 0px, transparent 50%),
      radial-gradient(at 100% 100%, rgba(217, 70, 239, 0.05) 0px, transparent 50%);
  }

  .text-gradient {
    @apply bg-clip-text text-transparent bg-gradient-to-r from-primary-600 to-accent-600;
  }

  .btn-primary {
    @apply px-6 py-3 bg-primary-600 text-white rounded-xl font-semibold
           hover:bg-primary-700 active:scale-[0.98]
           transition-all duration-200 shadow-sm hover:shadow-md;
  }

  .btn-secondary {
    @apply px-6 py-3 bg-white text-neutral-900 rounded-xl font-semibold border border-neutral-200
           hover:bg-neutral-50 active:scale-[0.98]
           transition-all duration-200 shadow-sm;
  }

  .btn-ghost {
    @apply px-6 py-3 bg-transparent text-neutral-700 rounded-xl font-semibold
           hover:bg-neutral-100 active:scale-[0.98]
           transition-all duration-200;
  }

  .input-modern {
    @apply w-full px-4 py-3 bg-white border border-neutral-300 rounded-xl
           focus:ring-2 focus:ring-primary-500/20 focus:border-primary-500
           transition-all duration-200 placeholder:text-neutral-400
           hover:border-neutral-400;
  }

  .card-modern {
    @apply bg-white rounded-2xl p-6 shadow-sm hover:shadow-md
           transition-all duration-200 border border-neutral-100;
  }

  .section-container {
    @apply max-w-7xl mx-auto px-4 sm:px-6 lg:px-8;
  }
}

@layer utilities {
  .transition-smooth {
    @apply transition-all duration-300 ease-out;
  }

  .transition-quick {
    @apply transition-all duration-150 ease-out;
  }
}

src/components/ui/Card.jsx.content:
import React from 'react';

const Card = ({ 
  children, 
  variant = 'default',
  hover = false,
  padding = true,
  className = '',
  onClick,
  ...props 
}) => {
  const baseStyles = 'rounded-2xl border transition-all duration-300';

  const variants = {
    default: 'bg-white border-neutral-100 shadow-soft',
    glass: 'bg-white/70 backdrop-blur-xl border-white/20 shadow-soft',
    elevated: 'bg-white border-neutral-100 shadow-medium',
    outline: 'bg-transparent border-neutral-200',
  };

  const hoverStyles = hover ? 'hover:shadow-medium hover:-translate-y-1 cursor-pointer' : '';
  const paddingStyles = padding ? 'p-6' : '';

  return (
    <div
      onClick={onClick}
      className={`
        ${baseStyles}
        ${variants[variant]}
        ${hoverStyles}
        ${paddingStyles}
        ${className}
      `}
      {...props}
    >
      {children}
    </div>
  );
};

export default Card;

