const AvatarChat = ({ color, text, size = "md", url }) => {
  const sizeClasses = {
    sm: "h-8 w-8",
    md: "h-10 w-10",
    lg: "h-12 w-12",
  };

  if (!color) {
    color = [
      "bg-green-400",
      "bg-blue-400",
      "bg-cyan-400",
      "bg-yellow-400",
      "bg-purple-400",
    ][Math.floor(Math.random() * 5)];
  }

  return url ? (
    <img
      src={url}
      alt="Avatar"
      className={`${sizeClasses[size]} rounded-full object-cover`}
    />
  ) : (
    <div
      className={`${color} ${sizeClasses[size]} rounded-full flex items-center justify-center text-white font-bold`}
    >
      <span>{text}</span>
    </div>
  );
};

const AvatarPerson = ({ size = "md", person }) => {
  // console.log(person);


  const sizeClasses = {
    sm: "h-8 w-8",
    md: "h-10 w-10",
    lg: "h-12 w-12",
  };

  return person ? (
    <img
      src={person?.profile_picture}
      alt="Avatar"
      className={`${sizeClasses[size]} rounded-full object-cover`}
    />
  ) : (
    <div
      className={`${sizeClasses[size]} rounded-full flex items-center justify-center text-white font-bold`}
    >
      <span>{person?.given_name}</span>
    </div>
  );
};

const IconButton = ({ icon, onClick, color = "text-text-secondary dark:text-text-secondary" }) => {
  return (
    <button
      className={`p-2 rounded-full hover:bg-gray-100 dark:hover:bg-slate-700 transition duration-200 ${color}`}
